import React , {useState} from "react";



const handleSearch = (e) => {
    e.preventDefault();

    const apiKey= '0cde099c6cc14589a2e4e62fdb04061b';
    const url = `https://api.rawg.io/api/games? key=${apiKey}&search=${encodeURI(searchText)}`;
    fetch(url)
    .then( response => response.json())
    .then( data => { setGames(data.results)})
    .catch( () => {alert('La recherche est lancée!')});
}
const Home = () => {
    const [searchText, setSearchText] = useState('');
    //on utilise unnstate pour garder nos données
    const [games, setGames] = useState([
        { id: 1, name: "Jeux 1", rating: 4.6 },
        { id: 2, name: "Jeux 2", rating: 3.5 },
        { id: 3, name: "Jeux 3", rating: 4.2 },
        { id: 4, name: "Jeux 4", rating: 1.5 },
        { id: 5, name: "Jeux 5", rating: 3.7 },
        { id: 6, name: "Jeux 6", rating: 5 },
    ]);

    return (
        <>{/* Un fragment doit être ajouté pour ne retourner q'un seul composant*/}
            <form className="my-2 sm:w-full md:w2/3 mx-auto flex px-2 text-2xl" onSubmit={handleSearch}>
                <input type="text" className="form-control" autoFocus={true} onInput={ e => { setSearchText(e.target.value)}} value={searchText} placeholder="Rechercher"/>
                <button type="submit" className="bg-blue-700 rounder-r text-white px-4 py-2">Rechercher</button>
            </form>

            {/* Ajoutons notre liste */}
            <ul className="sm:w-full md:w2/3 mx-auto px-2 text-2xl">
                {games.map( game => (
                    <li className="py-2 px-4 border-b border-gray-500 flex" key={game.id}>
                        <Link to={`/details/${game.slug}`} className="flex" >
                        <img src={game.background_image} alt="" className="w-24 pr-2" />
                        <div className="text-2xl font-bold flex-grow">{game.name}</div>
                        <div>{game.rating}</div>
                        </Link>
                    </li>
                ))}
            </ul>
        </>
    );

}

export default Home;
